"use strict";

describe("imergo-om-base#UnknownError", () =>
{
    const assert = require("assert");
    const iob = require("..");

    it("should initialize an object of type UnknownError", () =>
    {
        const error = new iob.UnknownError();
        assert(error instanceof iob.UnknownError);
    });
    it("should initialize an object of type Error", () =>
    {
        const error = new iob.UnknownError();
        assert(error instanceof Error);
    });
    it("should not initialize an object of type RangeError", () =>
    {
        const error = new iob.UnknownError();
        assert.strictEqual(error instanceof RangeError, false);
    });
    it("should initialize an object of type UnknownError with default message", () =>
    {
        const error = new iob.UnknownError();
        assert.strictEqual(error.message, "Unknown error. Please check the stack trace.");
    });
    it("should initialize an object of type UnknownError with custom message", () =>
    {
        const error = new iob.UnknownError("Custom message");
        assert.strictEqual(error.message, "Custom message");
    });
    it("should check UnknownError default name", () =>
    {
        const error = new iob.UnknownError();
        assert.strictEqual(error.name, "UnknownError");
    });
});