"use strict";

describe("imergo-om-base#UnsupportedPlatformError", () =>
{
    const assert = require("assert");
    const iob = require("..");

    it("should initialize an object of type UnsupportedPlatformError", () =>
    {
        const error = new iob.UnsupportedPlatformError();
        assert(error instanceof iob.UnsupportedPlatformError);
    });
    it("should initialize an object of type Error", () =>
    {
        const error = new iob.UnsupportedPlatformError();
        assert(error instanceof Error);
    });
    it("should not initialize an object of type RangeError", () =>
    {
        const error = new iob.UnsupportedPlatformError();
        assert.strictEqual(error instanceof RangeError, false);
    });
    it("should initialize an object of type UnsupportedPlatformError with default message", () =>
    {
        const error = new iob.UnsupportedPlatformError();
        assert.strictEqual(error.message, "The platform used to run the application is not supported.");
    });
    it("should initialize an object of type UnsupportedPlatformError with custom message", () =>
    {
        const error = new iob.UnsupportedPlatformError("Custom message");
        assert.strictEqual(error.message, "Custom message");
    });
    it("should check UnsupportedPlatformError default name", () =>
    {
        const error = new iob.UnsupportedPlatformError();
        assert.strictEqual(error.name, "UnsupportedPlatformError");
    });
});