"use strict";

describe("imergo-om-base#WrongNumberArgumentsError", () =>
{
    const assert = require("assert");
    const iob = require("..");

    it("should initialize an object of type WrongNumberArgumentsError", () =>
    {
        const error = new iob.WrongNumberArgumentsError();
        assert(error instanceof iob.WrongNumberArgumentsError);
    });
    it("should initialize an object of type Error", () =>
    {
        const error = new iob.WrongNumberArgumentsError();
        assert(error instanceof Error);
    });
    it("should initialize an object of type RangeError", () =>
    {
        const error = new iob.WrongNumberArgumentsError();
        assert.strictEqual(error instanceof RangeError, true);
    });
    it("should initialize an object of type WrongNumberArgumentsError with default message", () =>
    {
        const error = new iob.WrongNumberArgumentsError();
        assert.strictEqual(error.message, "Wrong number of arguments in the called function.");
    });
    it("should initialize an object of type WrongNumberArgumentsError with custom message", () =>
    {
        const error = new iob.WrongNumberArgumentsError("Custom message");
        assert.strictEqual(error.message, "Custom message");
    });
    it("should check WrongNumberArgumentsError default name", () =>
    {
        const error = new iob.WrongNumberArgumentsError();
        assert.strictEqual(error.name, "WrongNumberArgumentsError");
    });
});